import './Profile.css';


class Profile extends Component {

    constructor(props){
        super(props);
        this.state = {
            name : props.name,
            lastName :props.lastName,
            age:props.age,
            rating:props.rating,
            description:props.description,
            pic:props.pic,
        };
    }
    render() {
        var s="";
        var t=this.state.rating
        for(let i=0;i<t;i++)
        {
            s+= '*';
        }
      return(
          /*
        <div id="profile">
            <img  id="profilePicture" alt="profile" src ={this.state.pic} />
            <p>
                name = {this.state.name}  {this.state.lastName}
            </p>
            <p>
                age = {this.state.age}
            </p>
            <p>
            {s}
            </p>
            <button>update details</button>
        </div>
        */

        <div>

         <div id="header1"></div>
          <img id="avatar"  alt="t" src="https://bootdey.com/img/Content/avatar/avatar6.png"/>
          <div id="body">
            <div id="bodyContent">
              <p id="name">John Doe</p>
              <p id="info">UX Designer / Mobile developer</p>
              <p id="description">Lorem ipsum dolor sit amet, saepe sapientem eu nam. Qui ne assum electram expetendis, omittam deseruisse consequuntur ius an,</p>
              
              <button id="buttonContainer">
                <p>Opcion 1</p>  
              </button>              
              <button id="buttonContainer">
                <p>Opcion 2</p> 
              </button>


              </div>
              </div>
              </div>
      );
    }
  }

export default Profile;
