import React, { Component } from 'react';
import './Profile.css';


class Profile extends Component {

    constructor(props){
        super(props);
        this.state = {
            name : props.name,
            lastName :props.lastName,
            age:props.age,
            rating:props.rating,
            description:props.description,
            pic:props.pic,
        };
    }
    render() {
        var s="";
        var t=this.state.rating
        for(let i=0;i<t;i++)
        {
            s+= '*';
        }
      return(
        <div id="profile">
            <img  id="profilePicture" alt="profile" src ={this.state.pic} />
            <p>
                name = {this.state.name}  {this.state.lastName}
            </p>
            <p>
                age = {this.state.age}
            </p>
            <p>
            {s}
            </p>
            <button>update details</button>

            <figure class="snip1580"><img src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/331810/sample106.jpg" alt="profile-sample2"/>
  <figcaption>
    <h3>Giles Posture</h3>
    <h5>UX Design</h5>
  </figcaption><a href="#"></a>
</figure>
<figure class="snip1580 hover"><img src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/331810/sample116.jpg" alt="profile-sample7"/>
  <figcaption>
    <h3>Wisteria Ravenclaw</h3>
    <h5>Sound &amp; Vision</h5>
  </figcaption><a href="#"></a>
</figure>
<figure class="snip1580"><img src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/331810/sample117.jpg" alt="profile-sample6"/>
  <figcaption>
    <h3>Gunther Beard</h3>
    <h5>Web Development</h5>
  </figcaption><a href="#"></a>
</figure>
        </div>
      );
    }
  }

export default Profile;
